BEGIN;

CREATE TABLE size_scheme_variant_size (
    id serial primary key unique,
    size_scheme_id integer references size_scheme(id) deferrable NOT NULL,
    size_id integer references size(id) deferrable NOT NULL,
    designer_size_id integer references size(id) deferrable NOT NULL,
    position integer not null
);


ALTER TABLE public.size_scheme_variant_size OWNER TO postgres;

SELECT pg_catalog.setval('size_scheme_variant_size_id_seq', 425, true);


GRANT ALL ON TABLE size_scheme_variant_size TO postgres;
GRANT ALL ON TABLE size_scheme_variant_size TO www;
GRANT SELECT ON TABLE size_scheme_variant_size TO perlydev;

GRANT ALL ON SEQUENCE size_scheme_variant_size_id_seq TO postgres;
GRANT ALL ON SEQUENCE size_scheme_variant_size_id_seq TO www;
GRANT SELECT ON SEQUENCE size_scheme_variant_size_id_seq TO perlydev;

COMMIT;
